function sanitize(string) {
    if (typeof string == "string") {
      const map = {
        "<": "&lt;",
        ">": "&gt;",
        '"': "&quot;",
        "'": "&#x27;",
        "/": "&sol;",
        "\\": "&bsol;",
        ";": "&semi;",
        ":": "&colon;",
        "(": "&lpar;", 
        ")": "&rpar;",
        "[": "&lsqb;",
        "]": "&rsqb;",
        "{": "&lcub;",
        "}": "&rcub;",
      };
      var reg = /[<>"']/gi;
      return string.replace(reg, function (match) {
        return map[match];
      });
    } else {
      return string;
    }
  };

