function isFormValid(form) {
  arr = [];
  form.find("[data-validation]").each(function (index, val) {
    if ($(this).is(':visible')) {
      $(this).trigger('input');
      var valid = $(this).parents().hasClass("valid");
      arr.push(valid);
    }
  });
  var formValid;
  if (arr.includes(false)) {
    formValid = false;
  } else {
    formValid = true;
  }
  if (formValid) {
    form.addClass("validForm");
    return true;
  } else {
    try {
      form.removeClass("validForm");
      return false;
    } catch (error) {
      console.log(error);
    }
  }
}

